import React from 'react';


{
    const ELEMENTS = document.querySelectorAll(".face");
    ELEMENTS.forEach(element => {
       element.addEventListener('click', () => {
        console.log("j'ai cliqué");
            const debug = element.parentElement.parentElement;
            console.log(debug);
          debug.classList.add("container2");
       })
    });

}

const Cube = () => {
    return (
        <div className='cube'>





            <div className="container">
                <div className="container-cube">
                  <div className="container-cube-front face">
                        HTML / CSS / SCSS
                    </div>
                    <div className="container-cube-back  face">
                        JavaScript / TypeScript
                    </div>

                    <div className="container-cube-left face">
                        Php / SQL
                    </div>
                    <div className="container-cube-right face">
                        Node.js / React
                    </div>

                    <div className="container-cube-top  face">
                        jQuery / Bootstrap
                    </div>
                    <div className="container-cube-bot face">
                        Linux / Windows / MacOS
                    </div>
                </div>

            </div>
        </div>
    );
};



export default Cube;
